import React, {ChangeEvent, ChangeEventHandler, FC, useMemo, useState} from 'react';
import Card from "../../common/Card/Card";
import {ITrip} from "../../../types/types";
import FilterMenu from "../../common/FilterMenu/FilterMenu";

interface IPropTypes {
    trips: ITrip[]
}

const MainPage: FC<IPropTypes> = ({trips}) => {
    const [filters, setFilters] = useState({
        duration: '',
        level: ''
    });
    const changeFilter = (e:ChangeEvent<HTMLSelectElement>) => {
        setFilters({...filters, [e.target.name]: e.target.value});
    }
    const filteredTrips = useMemo(() => {
        let filteredArr = trips;
        if (!filters.duration && !filters.level) {
            return filteredArr;
        }
        if (filters.duration) {
            const greaterAndSmaller = filters.duration.split('_x_');
            console.log((greaterAndSmaller))
            filteredArr =  greaterAndSmaller.length === 2 ?
                trips.filter(item => item.duration > Number(greaterAndSmaller[0]) && item.duration < Number(greaterAndSmaller[1]))
                : trips.filter(item => item.duration >= Number(greaterAndSmaller[0]))
        }
        if (filters.level) {
            filteredArr = trips.filter(item => item.level === filters.level);
        }
        return filteredArr;
    }, [filters])

    return (
        <main>
            <FilterMenu changeFilter={changeFilter} />
            <ul className="trip-list">
                {filteredTrips?.map(trip => <Card key={trip.id} trip={trip}/>)}
            </ul>
        </main>
    );
};

export default MainPage;
