import React, {useEffect, useState} from 'react';
import {BrowserRouter, Route, Routes} from "react-router-dom";
import Layout from "../../page/Layout/Layout";
import SignUp from "../../page/SignUp/SignUp";
import SignIn from "../../page/SignIn/SignIn";
import Bookings from "../../page/Bookings/Bookings";
import TripPage from "../../page/TripPage/TripPage";
import MainPage from "../../page/MainPage/MainPage";
import {IRoute, PathEnum} from "../../../Router/Routes";
import jsonCards from '../../../data/cards.json';
import {ITrip} from "../../../types/types";


const RouterComponent = () => {
    const [trips, setTrips] = useState<ITrip[]>([]);
    useEffect(() => {
        setTrips(JSON.parse(JSON.stringify(jsonCards)));
    }, []);
    const test: ITrip[] =JSON.parse(JSON.stringify(jsonCards));
    const routes: IRoute[] = [
        {path: PathEnum.signUp, renderFunc: () => <SignUp/>},
        {path: PathEnum.signIn, renderFunc: () => <SignIn/>},
        {path: PathEnum.bookings, renderFunc: () => <Bookings trips={trips}/>},
        {path: PathEnum.tripInfo, renderFunc: () => <TripPage trips={test}/>},
        {index: true, renderFunc: () => <MainPage trips={trips}/>},
    ];
    if (jsonCards.length) {
        console.log(jsonCards)
        return (
            <div className="markup">
                <BrowserRouter>
                    <Routes>
                        <Route path='/' element={<Layout/>}>
                            {routes.map(route => route.index
                                ? <Route key='index' index element={route.renderFunc()}/>
                                : <Route key={route.path} path={route.path} element={route.renderFunc()}/>)}
                        </Route>
                    </Routes>
                </BrowserRouter>
            </div>
        );
    }
}
export default RouterComponent;
