import React, {ChangeEvent, ChangeEventHandler, FC, useMemo, useState} from 'react';
import Card from "../../common/Card/Card";
import {ITrip} from "../../../types/types";
import FilterMenu from "../../common/FilterMenu/FilterMenu";
import {IFilter, useFiltration} from "../../../hooks/useFiltration";

interface IPropTypes {
    trips: ITrip[]
}

const MainPage: FC<IPropTypes> = ({trips}) => {
    const [filters, setFilters] = useState<IFilter>({
        duration: '',
        level: ''
    });
    const [searchedValue, setSearchedValue] = useState<string>('');
    const changeFilter = (e:ChangeEvent<HTMLSelectElement>):void => {
        setFilters({...filters, [e.target.name]: e.target.value});
    }
    const changeSearch = (e:ChangeEvent<HTMLInputElement>):void => {
        setSearchedValue(e.target.value);
    }
    const filteredTrips: ITrip[] = useFiltration(trips, filters);
    const filteredAndSearchedTrips: ITrip[] = useMemo(() => {
        return filteredTrips.filter(item => item.title.includes(searchedValue))
    }, [filteredTrips, searchedValue])

    return (
        <main>
            <FilterMenu changeFilter={changeFilter} setSearchedValue={changeSearch} />
            <ul className="trip-list">
                {filteredAndSearchedTrips?.map(trip => <Card key={trip.id} trip={trip}/>)}
            </ul>
        </main>
    );
};

export default MainPage;
