import React, {ChangeEvent, FC, useState} from 'react';
import {Link} from "react-router-dom";
import {useValidateForm, validateType} from "../../../hooks/useValidateForm";

interface IProps {
    setLogin: (login: string) => void
}

const SignIn: FC<IProps> = ({setLogin}) => {
    const [loginValue, setLoginValue] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const setEmail = (e: ChangeEvent<HTMLInputElement>) => setLoginValue(e.target.value);
    const setPass = (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value);
    const [isValid, onSubmitForm]: validateType = useValidateForm(setLogin, password);
    return (
        <main className="sign-in-page">
            <h1 className="visually-hidden">Travel App</h1>
            <form className="sign-in-form" autoComplete="off" onSubmit={e => onSubmitForm(e, loginValue)}>
                <h2 className="sign-in-form__title">Sign In</h2>
                <label className="trip-popup__input input">
                    <span className="input__heading">Email</span>
                    <input name="email" type="email" value={loginValue} onChange={e => setEmail(e)} required/>
                </label>
                <label className="trip-popup__input input">
                    <span className="input__heading">Password</span>
                    <input name="password" type="password" onChange={e => setPass(e)} value={password}
                           autoComplete="new-password" required/>
                </label>
                <button className="button" disabled={!isValid} type="submit">Sign In</button>
            </form>
            <span>
        Already have an account?
        <Link to="/sign-up" className="sign-in-form__link">Sign Up</Link>
      </span>
        </main>
    );
};

export default SignIn;
