import React, {ChangeEvent, FC} from 'react';
import MySelect from "../MySelect/MySelect";
import {durationOptions, levelOptions} from "./options";

interface IProps {
    changeFilter: (e: ChangeEvent<HTMLSelectElement>) => void,
    setSearchedValue: (e:ChangeEvent<HTMLInputElement>) => void
}

const FilterMenu:FC<IProps> = ({changeFilter, setSearchedValue}) => {
    return (
        <section className="trips-filter">
            <h2 className="visually-hidden">Trips filter</h2>
            <form className="trips-filter__form" autoComplete="off">
                <label className="trips-filter__search input">
                    <span className="visually-hidden">Search by name</span>
                    <input onChange = {setSearchedValue} name="search" type="search" placeholder="search by title"/>
                </label>
                <label className="select">
                    <span className="visually-hidden">Search by duration</span>
                    <MySelect name='duration' options={durationOptions} changeFilter={changeFilter} />
                </label>
                <label className="select">
                    <span className="visually-hidden">Search by level</span>
                    <MySelect name='level' options={levelOptions} changeFilter={changeFilter} />
                </label>
            </form>
        </section>
    );
};

export default FilterMenu;
