import React, {FC, useMemo} from 'react';
import {IBookedTrip} from "../../../types/types";

interface IProps {
    booked: IBookedTrip,
    deleteBooking: (s:string) => void
}

const BookingComponent: FC<IProps> = ({booked, deleteBooking}) => {
    const dateNormal = useMemo(() => {
        const date = new Date(booked.date);
        const day:string = date.getDate().toString().length === 1 ? `0${date.getDate()}` : date.getDate().toString();
        const month:string = date.getMonth().toString().length === 1 ? `0${date.getMonth()}` : date.getMonth().toString();
        const year:string = date.getFullYear().toString();
        return `${day}.${month}.${year}`;
    }, [booked]);
    return (
        <li className="booking">
            <h3 className="booking__title">{booked.trip.title}</h3>
            <span className="booking__guests">{booked.guests} guests</span>
            <span className="booking__date">{dateNormal}</span>
            <span className="booking__total">{booked.totalPrice} $</span>
            <button className="booking__cancel" title="Cancel booking" onClick={() => deleteBooking(booked.id)}>
                <span className="visually-hidden">Cancel booking</span>
                ×
            </button>
        </li>
    );
};

export default BookingComponent;
