import React, {ChangeEvent, ChangeEventHandler, FC, useMemo, useState} from 'react';
import Card from "../../common/Card/Card";
import {ITrip} from "../../../types/types";
import FilterMenu from "../../common/FilterMenu/FilterMenu";
import {IFilter, useFiltration} from "../../../hooks/useFiltration";

interface IPropTypes {
    trips: ITrip[]
}

const MainPage: FC<IPropTypes> = ({trips}) => {
    const [filters, setFilters] = useState<IFilter>({
        duration: '',
        level: ''
    });
    const changeFilter = (e:ChangeEvent<HTMLSelectElement>) => {
        setFilters({...filters, [e.target.name]: e.target.value});
    }
    const filteredTrips = useFiltration(trips, filters)

    return (
        <main>
            <FilterMenu changeFilter={changeFilter} />
            <ul className="trip-list">
                {filteredTrips?.map(trip => <Card key={trip.id} trip={trip}/>)}
            </ul>
        </main>
    );
};

export default MainPage;
