import React, {FC, useEffect, useMemo} from 'react';
import {useNavigate, useParams} from "react-router-dom";
import {ITrip} from "../../../types/types";

interface IPropTypes {
    trips: ITrip[]
}

const TripPage:FC<IPropTypes> = ({trips}) => {
    type paramsId = {
        id: string
    }
    const navigate = useNavigate();
    const params = useParams<paramsId>();
    const tripItem: ITrip | undefined = useMemo(() => {
        return trips.find(trip => trip.id === params.id);
    }, []);
    useEffect(() => {
        if(!tripItem) {
            navigate('/', {replace: true})
        }
    }, []);
    return (
        <div className="trip__content">
            <div className="trip-info">
                <h3 className="trip-info__title">{tripItem?.title}</h3>
                <div className="trip-info__content">
                    <span className="trip-info__duration"><strong>{tripItem?.duration}</strong> days</span>
                    <span className="trip-info__level">easy</span>
                </div>
            </div>
            <div className="trip__description">
                {tripItem?.duration}
            </div>
            <div className="trip-price">
                <span>Price</span>
                <strong className="trip-price__value">7000 $</strong>
            </div>
            <button className="trip__button button">Book a trip</button>
        </div>
    );
};

export default TripPage;
