import {FormEvent, useMemo} from "react";
import {useNavigate} from "react-router-dom";

type returnValidate = [boolean, (e: FormEvent<HTMLFormElement>, loginValue: string) => void];

interface IParams {
    setLogin: (l: string) => void,
    password: string
}

export const useValidateForm = <IParams>(setLogin, password: string): returnValidate => {
    const navigate = useNavigate();
    const isValid: boolean = useMemo(() => {
        return (password.length > 3 && password.length < 20)
    }, [password])
    const onSubmitForm = (e: FormEvent<HTMLFormElement>, loginValue: string): void => {
        e.preventDefault();
        setLogin(loginValue);
        navigate('/', {replace: true});
    }
    return [isValid, onSubmitForm];
}
